task descr: Composite slope and aspect layer
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003').clip(region);

// Compute slope and aspect
var terrain = ee.Terrain.products(dem);

// Add combined layer: slope and aspect bands
Map.addLayer(terrain.select(['slope']), {min: 0, max: 60, palette: ['white', 'red']}, 'Slope');
Map.addLayer(terrain.select(['aspect']), {min: 0, max: 360, palette: ['blue', 'green', 'yellow', 'red']}, 'Aspect');
