task descr: Calculate the residual by subtracting a reference elevation from the DEM
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003').clip(region);

// Compute residual: DEM - 500m
var residual = dem.subtract(500);

// Add residual layer
Map.addLayer(residual, {min: -500, max: 1500, palette: ['blue', 'white', 'brown']}, 'DEM - 500m Residual');
