task descr: Calculate the coefficient of variation of DEM
// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003');

// Define kernel size (window size in pixels)
var kernel = ee.Kernel.square({
  radius: 3,   // radius in pixels, e.g., 3 -> 7x7 window
  units: 'pixels',
  magnitude: 1
});

// Compute local mean
var mean = dem.reduceNeighborhood({
  reducer: ee.Reducer.mean(),
  kernel: kernel
});

// Compute local std dev
var std = dem.reduceNeighborhood({
  reducer: ee.Reducer.stdDev(),
  kernel: kernel
});

// Compute coefficient of variation (CV = std / mean)
var cv = std.divide(mean);

// Visualization parameters
var visParams = {
  min: 0,
  max: 1,
  palette: ['white', 'blue', 'green', 'yellow', 'red']
};

// Display CV map
Map.centerObject(dem, 5);
Map.addLayer(cv, visParams, 'DEM CV');
