task descr: Crop the slope layer to the city boundary
// Define city boundary (example rectangle here)
var cityBoundary = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003');

// Compute slope
var slope = ee.Terrain.slope(dem);

// Clip slope to city boundary
var clippedSlope = slope.clip(cityBoundary);

// Add clipped slope layer
Map.addLayer(clippedSlope, {min: 0, max: 60, palette: ['white', 'red']}, 'Slope clipped to city boundary');
