task descr: Calculate the proportion of cells in a specific elevation range in the DEM
// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003');

// Define elevation range
var minElev = 800;
var maxElev = 1200;

// Define region
var region = ee.Geometry.Rectangle([100.5, 23.0, 101.5, 24.0]);

// Create mask
var inRange = dem.gte(minElev).and(dem.lte(maxElev));

// Count pixels
var targetCount = inRange.reduceRegion({
  reducer: ee.Reducer.sum(),
  geometry: region,
  scale: 30
});

var totalCount = dem.mask().reduceRegion({
  reducer: ee.Reducer.count(),
  geometry: region,
  scale: 30
});

// Compute ratio
var proportion = ee.Number(targetCount.get('elevation'))
  .divide(totalCount.get('elevation'))
  .multiply(100);

print('Proportion (%):', proportion);
