task descr: Calculate the maximum MODIS LST
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS LST
var lstCol = ee.ImageCollection('MODIS/061/MOD11A2')
  .select('LST_Day_1km')
  .filterBounds(region);

// Compute max LST
var lstMax = lstCol.max().multiply(0.02).clip(region);
Map.addLayer(lstMax, {min: 250, max: 320, palette: ['blue', 'green', 'red']}, 'LST Max');
