task descr: Filter MODIS LST time period
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load and filter MODIS LST
var lstCol = ee.ImageCollection('MODIS/061/MOD11A2')
  .select('LST_Day_1km')
  .filterDate('2020-06-01', '2020-08-31')
  .filterBounds(region);

print('Filtered LST collection:', lstCol);
