task descr: MODIS vegetation index threshold mask
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterBounds(region);

// NDVI threshold mask (NDVI > 4000)
var ndviImage = ndviCol.first();
var ndviMask = ndviImage.gt(4000).selfMask();
Map.addLayer(ndviMask, {palette: ['green']}, 'NDVI > 4000 Mask');
