task descr: Use vector zones to clip raster images
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI
var ndviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .first();

// Clip to vector region
var clippedNDVI = ndviImage.clip(vectorRegion);
Map.addLayer(clippedNDVI, {min: 0, max: 9000, palette: ['white', 'green']}, 'Clipped NDVI');
