task descr: Calculate the maximum NDVI value within the vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI
var ndviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .first();

// Compute max NDVI in vector region
var maxNDVI = ndviImage.reduceRegion({
  reducer: ee.Reducer.max(),
  geometry: vectorRegion,
  scale: 250,
  maxPixels: 1e9
});
print('Max NDVI:', maxNDVI);
