task descr: Calculate the minimum NDVI within the vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI
var ndviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .first();

// Compute min NDVI in vector region
var minNDVI = ndviImage.reduceRegion({
  reducer: ee.Reducer.min(),
  geometry: vectorRegion,
  scale: 250,
  maxPixels: 1e9
});
print('Min NDVI:', minNDVI);
