task descr: Map raster areas to vector
// Define region
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI
var ndviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .first()
  .clip(region);

// Create mask
var ndviMask = ndviImage.gt(4000).selfMask();

// Raster to vector
var vector = ndviMask.reduceToVectors({
  geometry: region,
  scale: 250,
  geometryType: 'polygon'
});
Map.addLayer(vector, {}, 'Vector from Raster');
