task descr: Statistics of area after raster clipping
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI
var ndviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .first()
  .clip(vectorRegion);

// Create mask
var ndviMask = ndviImage.gt(4000);

// Compute area
var area = ndviMask.multiply(ee.Image.pixelArea())
  .reduceRegion({
    reducer: ee.Reducer.sum(),
    geometry: vectorRegion,
    scale: 250,
    maxPixels: 1e9
});
print('Area NDVI > 4000 (m²):', area);
