task descr: Extract raster values at the centroid of vector zones
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI
var ndviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .first();

// Compute centroid
var centroid = vectorRegion.centroid();

// Sample raster at centroid
var value = ndviImage.sample({
  region: centroid,
  scale: 250
});

print('NDVI at centroid:', value);
