task descr: Calculate the mean of DEM in the vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003').clip(vectorRegion);

// Compute mean elevation
var meanElevation = dem.reduceRegion({
  reducer: ee.Reducer.mean(),
  geometry: vectorRegion,
  scale: 30,
  maxPixels: 1e9
});
print('Mean Elevation:', meanElevation);
