task descr: Calculate the average slope within the vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM and compute slope
var slope = ee.Terrain.slope(ee.Image('USGS/SRTMGL1_003')).clip(vectorRegion);

// Compute mean slope
var meanSlope = slope.reduceRegion({
  reducer: ee.Reducer.mean(),
  geometry: vectorRegion,
  scale: 30,
  maxPixels: 1e9
});
print('Mean Slope:', meanSlope);
