task descr: Calculate the mean NDWI within the vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load Landsat image
var landsat = ee.ImageCollection('LANDSAT/LC08/C01/T1_SR')
  .filterBounds(vectorRegion)
  .filter(ee.Filter.lt('CLOUD_COVER', 10))
  .first();

// Compute NDWI
var ndwi = landsat.normalizedDifference(['B3', 'B5']).rename('NDWI');

// Compute mean NDWI
var meanNDWI = ndwi.reduceRegion({
  reducer: ee.Reducer.mean(),
  geometry: vectorRegion,
  scale: 30,
  maxPixels: 1e9
});
print('Mean NDWI:', meanNDWI);
