task descr: Calculate the standard deviation of NDVI within the vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI
var ndviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .first()
  .clip(vectorRegion);

// Compute NDVI stdDev
var stdDevNDVI = ndviImage.reduceRegion({
  reducer: ee.Reducer.stdDev(),
  geometry: vectorRegion,
  scale: 250,
  maxPixels: 1e9
});
print('NDVI stdDev:', stdDevNDVI);
