task descr: Calculate the standard deviation of EVI within the vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS EVI
var eviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('EVI')
  .first()
  .clip(vectorRegion);

// Compute EVI stdDev
var stdDevEVI = eviImage.reduceRegion({
  reducer: ee.Reducer.stdDev(),
  geometry: vectorRegion,
  scale: 250,
  maxPixels: 1e9
});
print('EVI stdDev:', stdDevEVI);
