task descr: Calculate elevation range within vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003').clip(vectorRegion);

// Compute elevation range
var elevationStats = dem.reduceRegion({
  reducer: ee.Reducer.minMax(),
  geometry: vectorRegion,
  scale: 30,
  maxPixels: 1e9
});
var range = ee.Number(elevationStats.get('elevation_max'))
  .subtract(elevationStats.get('elevation_min'));
print('Elevation Range:', range);
