task descr: Extract the time of maximum NDVI in the vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI collection
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterBounds(vectorRegion);

// Map NDVI mean per image
var ndviMean = ndviCol.map(function(img) {
  var mean = img.reduceRegion({
    reducer: ee.Reducer.mean(),
    geometry: vectorRegion,
    scale: 250
  }).get('NDVI');
  return img.set('NDVI_mean', mean);
});

// Sort by NDVI_mean descending
var maxNdviImage = ndviMean.sort('NDVI_mean', false).first();
var maxDate = ee.Date(maxNdviImage.get('system:time_start')).format('YYYY-MM-dd');
print('Max NDVI Date:', maxDate);
