task descr: Calculate the annual average of NDVI in the vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI collection (2020)
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterDate('2020-01-01', '2020-12-31')
  .filterBounds(vectorRegion);

// Compute annual mean NDVI
var annualMeanNDVI = ndviCol.mean();

// Reduce to vector region
var meanNDVI = annualMeanNDVI.reduceRegion({
  reducer: ee.Reducer.mean(),
  geometry: vectorRegion,
  scale: 250,
  maxPixels: 1e9
});
print('Annual Mean NDVI:', meanNDVI);
