task descr: Calculate seasonal average of NDVI in the vector area
// Define vector region
var vectorRegion = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load MODIS NDVI collection (summer 2020)
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterDate('2020-06-01', '2020-08-31')
  .filterBounds(vectorRegion);

// Compute seasonal mean NDVI
var seasonalMeanNDVI = ndviCol.mean();

// Reduce to vector region
var meanNDVI = seasonalMeanNDVI.reduceRegion({
  reducer: ee.Reducer.mean(),
  geometry: vectorRegion,
  scale: 250,
  maxPixels: 1e9
});
print('Seasonal Mean NDVI:', meanNDVI);
