task descr: Use layer groups to manage multiple layers
var slope = ee.Terrain.slope(ee.Image('USGS/SRTMGL1_003'));
var aspect = ee.Terrain.aspect(ee.Image('USGS/SRTMGL1_003'));

Map.layers().add(ui.Map.Layer(slope, {min: 0, max: 60, palette: ['white', 'red']}, 'Slope Layer'));
Map.layers().add(ui.Map.Layer(aspect, {min: 0, max: 360, palette: ['blue', 'green', 'yellow', 'red']}, 'Aspect Layer'));
