var MAP = ee.FeatureCollection("users/bidyutsarania/1k");

var tree = ee.Image("UMD/hansen/global_forest_change_2020_v1_8")
            .select(['treecover2000']).clip(MAP);

var forestArea = tree.multiply(ee.Image.pixelArea()).divide(10000);
var forestSize = forestArea.reduceRegion({
    reducer: ee.Reducer.sum(),
    geometry: MAP,
    scale: 30,
    maxPixels: 1e13
});
print('Year 2000 tree cover (ha) \nmeeting minimum canopy cover and \nforest area thresholds \n ', forestSize.get('treecover2000'));
