var point = ee.Geometry.Point([116.4, 39.9]);

var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterBounds(point);

var ndviChart = ui.Chart.image.series({
  imageCollection: ndviCol,
  region: point,
  reducer: ee.Reducer.mean(),
  scale: 250
});
print(ndviChart);
