var point = ee.Geometry.Point([116.4, 39.9]);

var lstCol = ee.ImageCollection('MODIS/061/MOD11A2')
  .select('LST_Day_1km')
  .filterBounds(point);

var lstChart = ui.Chart.image.series({
  imageCollection: lstCol.map(function(img) {
    return img.multiply(0.02).copyProperties(img, ['system:time_start']);
  }),
  region: point,
  reducer: ee.Reducer.mean(),
  scale: 1000
});
print(lstChart);
