task descr: Calculate the frequency of fire occurrences in a certain area
// Load MODIS active fire data
var fire = ee.FeatureCollection('MODIS/061/MCD14DL')
  .filterDate('2023-01-01', '2023-12-31');  // time range

// Define target region
var region = ee.Geometry.Rectangle([100.5, 23.0, 101.5, 24.0]);

// Filter fires in region
var fireInRegion = fire.filterBounds(region);

// Count number of fire points
var fireCount = fireInRegion.size();

// Print result
print('Number of fire points in region:', fireCount);
