var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterBounds(region);

var trendChart = ui.Chart.image.seriesByRegion({
  imageCollection: ndviCol,
  regions: ee.FeatureCollection([ee.Feature(region)]),
  reducer: ee.Reducer.mean(),
  band: 'NDVI',
  scale: 250,
  seriesProperty: 'system:time_start'
}).setChartType('ScatterChart').setOptions({
  trendlines: {0: {showR2: true, visibleInLegend: true}}
});

print(trendChart);
