var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

var ndwiCol = ee.ImageCollection('LANDSAT/LC08/C01/T1_SR')
  .filterBounds(region)
  .filter(ee.Filter.lt('CLOUD_COVER', 10))
  .map(function(img) {
    return img.normalizedDifference(['B3', 'B5']).rename('NDWI')
      .copyProperties(img, ['system:time_start']);
  });

var waterAreaChart = ui.Chart.image.series({
  imageCollection: ndwiCol.map(function(img) {
    var water = img.gt(0).selfMask();
    return water.multiply(ee.Image.pixelArea()).rename('WaterArea');
  }),
  region: region,
  reducer: ee.Reducer.sum(),
  scale: 30
}).setChartType('LineChart');

print(waterAreaChart);
