var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);
var dem = ee.Image('USGS/SRTMGL1_003').clip(region);

var ndvi = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .first().clip(region);

var elevationClass = dem.expression(
  '(b < 100) ? 1 : (b < 500) ? 2 : (b < 1000) ? 3 : 4',
  {'b': dem}).rename('elev_class');

var zonalStats = ndvi.addBands(elevationClass).reduceRegion({
  reducer: ee.Reducer.mean().group(1, 'elev_class'),
  geometry: region,
  scale: 250,
  maxPixels: 1e9
});
print('NDVI by Elevation:', zonalStats);
