var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);
var slope = ee.Terrain.slope(ee.Image('USGS/SRTMGL1_003')).clip(region);

var lst = ee.ImageCollection('MODIS/061/MOD11A2')
  .select('LST_Day_1km')
  .first().multiply(0.02).clip(region);

var slopeClass = slope.expression(
  '(b < 5) ? 1 : (b < 15) ? 2 : (b < 30) ? 3 : 4',
  {'b': slope}).rename('slope_class');

var zonalStats = lst.addBands(slopeClass).reduceRegion({
  reducer: ee.Reducer.mean().group(1, 'slope_class'),
  geometry: region,
  scale: 1000,
  maxPixels: 1e9
});
print('LST by Slope:', zonalStats);
