var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);
var aspect = ee.Terrain.aspect(ee.Image('USGS/SRTMGL1_003')).clip(region);

var lst = ee.ImageCollection('MODIS/061/MOD11A2')
  .select('LST_Day_1km')
  .first().multiply(0.02).clip(region);

var aspectClass = aspect.expression(
  '(b <= 45 || b > 315) ? 1 : (b <= 135) ? 2 : (b <= 225) ? 3 : 4',
  {'b': aspect}).rename('aspect_class');

var zonalStats = lst.addBands(aspectClass).reduceRegion({
  reducer: ee.Reducer.mean().group(1, 'aspect_class'),
  geometry: region,
  scale: 1000,
  maxPixels: 1e9
});
print('LST by Aspect:', zonalStats);
