var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterBounds(region);

var q1 = ndviCol.filterDate('2020-01-01', '2020-03-31').mean();
var q2 = ndviCol.filterDate('2020-04-01', '2020-06-30').mean();
var q3 = ndviCol.filterDate('2020-07-01', '2020-09-30').mean();
var q4 = ndviCol.filterDate('2020-10-01', '2020-12-31').mean();

Map.addLayer(q1, {min: 0, max: 9000, palette: ['white', 'green']}, 'NDVI Q1');
Map.addLayer(q2, {min: 0, max: 9000, palette: ['white', 'green']}, 'NDVI Q2');
Map.addLayer(q3, {min: 0, max: 9000, palette: ['white', 'green']}, 'NDVI Q3');
Map.addLayer(q4, {min: 0, max: 9000, palette: ['white', 'green']}, 'NDVI Q4');
