task descr: Classify the DEM by elevation levels
var dem = ee.Image('USGS/SRTMGL1_003');
var demClass = dem.expression(
  '(b < 100) ? 1 : (b < 500) ? 2 : (b < 1000) ? 3 : 4',
  {'b': dem}).rename('dem_class');
Map.addLayer(demClass, {min:1, max:4, palette:['blue','green','yellow','brown']}, 'DEM Classified');
