var aspect = ee.Terrain.aspect(ee.Image('USGS/SRTMGL1_003'));

var aspectClass = aspect.expression(
  '(b <= 45 || b > 315) ? 1 : (b <= 135) ? 2 : (b <= 225) ? 3 : 4',
  {'b': aspect}).rename('aspect_class');

Map.addLayer(aspectClass, {min:1, max:4, palette:['red','green','blue','yellow']}, 'Aspect Class');
