task descr: Mask areas with a slope greater than 25 degrees
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load DEM
var dem = ee.Image('USGS/SRTMGL1_003').clip(region);

// Compute slope
var slope = ee.Terrain.slope(dem);

// Mask slope > 25 deg
var slopeMask = slope.gt(25);
var maskedSlope = slope.updateMask(slopeMask);

// Add to map
Map.addLayer(maskedSlope, {min: 0, max: 60, palette: ['white', 'red']}, 'Slope > 25 deg');
