task descr: Randomly sample 100 points from the grid image
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load NDVI image and clip
var ndviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .first()
  .clip(region);

// Uniform sampling: randomPoints + sampleRegions
var points = ee.FeatureCollection.randomPoints(region, 100);

var samples = ndviImage.sampleRegions({
  collection: points,
  scale: 250,
  geometries: true
});

// Export samples to CSV
Export.table.toDrive({
  collection: samples,
  description: 'Uniform_100_Samples',
  fileFormat: 'CSV'
});
