task descr: Sample raster image by vector zone
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load NDVI image and clip
var ndviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .first()
  .clip(region);

// Sample image over vector region
var samples = ndviImage.sample({
  region: region,
  scale: 250,
  geometries: true
});

// Export samples to CSV
Export.table.toDrive({
  collection: samples,
  description: 'Vector_Region_Samples',
  fileFormat: 'CSV'
});
