// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load an NDVI image
var ndviImage = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterDate('2020-07-01', '2020-07-31')
  .first();

// Generate random points for sampling
var points = ee.FeatureCollection.randomPoints(region, 100);

// Sample image at points
var samples = ndviImage.sampleRegions({
  collection: points,
  scale: 250,
  geometries: true
});

// Export sampling results to CSV
Export.table.toDrive({
  collection: samples,
  description: 'NDVI_Samples_Export',
  fileFormat: 'CSV'
});
