task descr: Export the image cropping result as GeoTIFF
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load and filter Landsat image collection
var landsatCol = ee.ImageCollection('LANDSAT/LC08/C01/T1_SR')
  .filterDate('2020-05-01', '2020-09-30')
  .filterBounds(region)
  .sort('CLOUD_COVER');

// Get first image and clip to region
var clippedImage = landsatCol.first().clip(region);

// Export clipped image as GeoTIFF
Export.image.toDrive({
  image: clippedImage,
  description: 'Clipped_Landsat_Image',
  scale: 30,
  region: region,
  fileFormat: 'GeoTIFF'
});
