task descr: Export NDVI time series to CSV
// Assume NDVI collection is ready: ndviCollection
var ndviCollection = ee.ImageCollection('MODIS/061/MOD13A2').select('NDVI');

var ndviMean = ndviCollection.map(function(img) {
  return ee.Feature(null, {
    'date': img.date().format('YYYY-MM-dd'),
    'NDVI': img.reduceRegion({
      reducer: ee.Reducer.mean(),
      geometry: img.geometry(),  // Use image bounds
      scale: 500,
      maxPixels: 1e13
    }).get('NDVI')
  });
});

Export.table.toDrive({
  collection: ee.FeatureCollection(ndviMean),
  description: 'NDVI_TimeSeries',
  fileFormat: 'CSV'
});
