task descr: Export the spatial mean of NDVI within the vector area as CSV
// Define region of interest
var region = ee.Geometry.Rectangle([116.3, 39.8, 116.5, 40.0]);

// Load NDVI image collection
var ndviCol = ee.ImageCollection('MODIS/061/MOD13Q1')
  .select('NDVI')
  .filterDate('2020-01-01', '2020-12-31')
  .filterBounds(region);

// Map over collection to compute mean NDVI per image
var ndviMean = ndviCol.map(function(img) {
  var mean = img.reduceRegion({
    reducer: ee.Reducer.mean(),
    geometry: region,
    scale: 250
  }).get('NDVI');
  return ee.Feature(null, {
    'date': img.date().format('YYYY-MM-dd'),
    'NDVI_mean': mean
  });
});

// Export to CSV
Export.table.toDrive({
  collection: ndviMean,
  description: 'NDVI_Mean_Export',
  fileFormat: 'CSV'
});
