/**** Start of imports. If edited, may not auto-convert in the playground. ****/
var gsw = ee.Image("JRC/GSW1_0/GlobalSurfaceWater"),
    geometry = /* color: #98ff00 */ee.Geometry.MultiPoint(),
    roi = ee.FeatureCollection("projects/ee-solar-suitability-iwmi/assets/Part01");
/***** End of imports. If edited, may not auto-convert in the playground. *****/

var occurrence = gsw.select('occurrence');
Map.addLayer(occurrence);

var transition = gsw.select('transition');

Map.setCenter(23.781, 2.945, 4);     // Mekong River Basin, SouthEast Asia
Map.addLayer({
  eeObject: transition,
  name: 'Transition classes (1984-2015)',
});

var area_image_with_transition_class = ee.Image.pixelArea().addBands(transition);

var reduction_results = area_image_with_transition_class.reduceRegion({
  reducer: ee.Reducer.sum().group({
    groupField: 1,
    groupName: 'transition_class_value',
  }),
  geometry: roi,
  scale: 30,
  bestEffort: true,
});
print('reduction_results', reduction_results);

// Export the image, specifying the CRS, transform, and region.
Export.image.toDrive({
  image: transition,
  description: 'SurfaceWaterOccurance',
  crs: 'EPSG:4326',
  scale: 30,
  folder: 'SurfaceWaterOccurance',
  // crsTransform: projection.transform,
  maxpixels: 10e
  region: roi
});
