// GEE Code: Nanjing Building Analysis

// 1. 导入Nanjing建筑数据
var buildingData = ee.FeatureCollection("users/your_username/NanjingBuilding");  // 修改为实际数据路径

// 2. 分析建筑功能（按'landuseTyp'统计建筑数量）
var landuseCount = buildingData.aggregate_stats('landuseTyp');
print('Building function summary:', landuseCount);

// 3. 计算建筑物的平均年龄和楼层数
var stats = buildingData.reduceColumns({
  reducer: ee.Reducer.mean(),
  selectors: ['Age', 'Fnum']
});
print('Average Age:', stats.get('mean_Age'));
print('Average Floor Count:', stats.get('mean_Fnum'));

// 4. 按功能类型计算平均年龄
var avgAgeByLanduse = buildingData.reduceColumns({
  reducer: ee.Reducer.mean().group({
    groupField: 0,
    groupName: 'landuseTyp'
  }),
  selectors: ['landuseTyp', 'Age']
});
print('Average Age by Function:', avgAgeByLanduse);

// 5. 相关性分析（建筑年龄与楼层数之间的相关性）
var correlation = buildingData.reduceColumns({
  reducer: ee.Reducer.pearsonsCorrelation(),
  selectors: ['Age', 'Fnum']
});
print('Correlation between Age and Floor Count:', correlation);

// 6. 添加并更新平均年龄字段
var addAvgAge = buildingData.map(function(feature) {
  var landuse = feature.get('landuseTyp');
  var avgAge = avgAgeByLanduse[landuse];
  return feature.set('AvgAge', avgAge);
});
print('Updated Feature Collection with AvgAge:', addAvgAge);

// 7. 空间模式分析（建筑功能的空间分布）
var spatialPatterns = buildingData.aggregate_stats('landuseTyp');
print('Spatial Distribution of Functions:', spatialPatterns);

// 可视化
Map.centerObject(buildingData, 12);
Map.addLayer(buildingData, {}, 'Building Data');
