// ================== Module1: Environment Setup ==================
// 加载夜间灯光数据和城市区域矢量数据
var nightlight = ee.Image("users/your_username/LongNTL_2023_BTH");  // 上传的夜间灯光数据
var urbanAreas = ee.FeatureCollection("users/your_username/BTH");  // 上传的城市区域矢量数据

// ================== Module2: Load and Clip Data ==================
// 使用城市区域边界裁剪夜间灯光影像
var clippedNightlight = nightlight.clip(urbanAreas);

// ================== Module3: Statistical Analysis ==================
// 计算夜间灯光强度统计信息（均值、标准差、最大值）
var meanIntensity = clippedNightlight.reduceRegion({
  reducer: ee.Reducer.mean(),
  geometry: urbanAreas,
  scale: 30,  // 根据数据的分辨率调整
  maxPixels: 1e8
});

var stdIntensity = clippedNightlight.reduceRegion({
  reducer: ee.Reducer.stdDev(),
  geometry: urbanAreas,
  scale: 30,
  maxPixels: 1e8
});

var maxIntensity = clippedNightlight.reduceRegion({
  reducer: ee.Reducer.max(),
  geometry: urbanAreas,
  scale: 30,
  maxPixels: 1e8
});

print("Nightlight Intensity Statistics:", meanIntensity);
print("Standard Deviation:", stdIntensity);
print("Max Intensity:", maxIntensity);

// ================== Module4: Visualization ==================
// 可视化夜间灯光强度的空间分布
Map.centerObject(urbanAreas, 10);  // 根据城市区域居中地图
Map.addLayer(clippedNightlight, {min: 0, max: 100, palette: ['black', 'yellow', 'white']}, "Nightlight Intensity");

// ================== Module5: Histogram Analysis ==================
// 计算夜间灯光强度的直方图
var hist = clippedNightlight.reduceRegion({
  reducer: ee.Reducer.histogram(),
  geometry: urbanAreas,
  scale: 30,
  maxPixels: 1e8
});

print("Nightlight Intensity Histogram:", hist);
