var region = ee.FeatureCollection(table);
var mod13 = ee.ImageCollection('MODIS/006/MOD13Q1').filter(ee.Filter.calendarRange(6,8,'month')).filter(ee.Filter.calendarRange(2000,2020,'year'));
var summerStats = ee.ImageCollection(ee.Join.saveAll('same_year').apply({
  primary: mod13.distinct('year'),
  secondary: mod13,
  condition: ee.Filter.equals({leftField: 'year', rightField: 'year'})
}).map(function(img) {
  return ee.Image.cat(ee.Image.constant(img.get('year')).toShort(), ee.ImageCollection.fromImages(img.get('same_year')).select('NDVI').max())
}));
var sens = summerStats.reduce(ee.Reducer.sensSlope());
var type = sens.select('slope').gt(0).multiply(1).add(sens.select('slope').lte(0).multiply(2));
type.eq(1).updateMask(type.eq(1)).multiply(ee.Image.pixelArea()).reduceRegion({
  reducer: ee.Reducer.sum(),
  geometry: region,
  scale: 250
});
