// ================== Module1: Environment Setup ==================
var pop_2010 = ee.Image('users/your_username/Beijing2010');
var pop_2020 = ee.Image('users/your_username/Beijing2020');

// ================== Module3: Percentage Change Calculation ==================
var percentage_change = pop_2020.subtract(pop_2010).divide(pop_2010.max(1)).multiply(100);

// ================== Module4: Statistical Analysis ==================
var stats_percentage_change = percentage_change.reduceRegion({
  reducer: ee.Reducer.mean().combine({
    reducer2: ee.Reducer.stdDev(),
    sharedInputs: true
  }),
  geometry: pop_2010.geometry(),
  scale: 30,
  maxPixels: 1e8
});

var stats_2010 = pop_2010.reduceRegion({
  reducer: ee.Reducer.mean().combine({
    reducer2: ee.Reducer.stdDev(),
    sharedInputs: true
  }),
  geometry: pop_2010.geometry(),
  scale: 30,
  maxPixels: 1e8
});

var stats_2020 = pop_2020.reduceRegion({
  reducer: ee.Reducer.mean().combine({
    reducer2: ee.Reducer.stdDev(),
    sharedInputs: true
  }),
  geometry: pop_2020.geometry(),
  scale: 30,
  maxPixels: 1e8
});

// ================== Module5: Visualization ==================
Map.centerObject(pop_2010, 10);
Map.addLayer(percentage_change, {
  min: -50,
  max: 50,
  palette: ['blue', 'white', 'red']
}, 'Percentage change');

var chart = ui.Chart.image.histogram(percentage_change, pop_2010.geometry(), 30)
  .setChartType('Column')
  .setOptions({
    title: 'Histogram of Population Change',
    vAxis: {title: 'Pixel count'},
    hAxis: {title: 'Percentage change'},
    colors: ['green']
  });
