var Hubei = ee.FeatureCollection(table);
var MODIS_LAI = ee.ImageCollection('MODIS/061/MOD15A2H')
                .select('Lai_500m')
                .filterBounds(Hubei)
                .filterDate('2011-01-01', '2021-12-31');

Map.centerObject(Hubei, 7);
Map.addLayer(Hubei, {color: 'FFFFFF'}, 'Hubei');

function createTimeBand(img) {
    var year = img.date().difference(ee.Date('1990-01-01'), 'year');
    var year_image = ee.Image(year).float().rename('Year');
    return year_image.addBands(img.multiply(0.1));
}

function getYearCol(sDate, eDate, lxCol, region) {
  var yearList = ee.List.sequence(ee.Date(sDate).get("year"), ee.Number(ee.Date(eDate).get("year")).subtract(1));
  var yearImgList = yearList.map(function(year) {
    year = ee.Number(year);
    var _sdate = ee.Date.fromYMD(year, 1, 1);
    var _edate = ee.Date.fromYMD(year.add(1), 1, 1);
    var tempCol = lxCol.filterDate(_sdate, _edate).map(createTimeBand);
    var img = tempCol.mean().clip(region);
    img = img.set("year", year);
    img = img.set("system:index", ee.String(year.toInt()));
    return img;
  });
  var yearImgCol = ee.ImageCollection.fromImages(yearImgList);
  return yearImgCol;
}

var yearImgCol = getYearCol('2011-01-01', '2022-01-01', MODIS_LAI, Hubei);
var fit = yearImgCol.reduce(ee.Reducer.linearFit());

var scale_fit = fit.select('scale');
var viz = {
  min: -0.0149,
  max: 0.0502,
  palette: [
    'FFFFFF', 'CE7E45', 'DF923D', 'F1B555', 'FCD163', '99B718', '74A901',
    '66A000', '529400', '3E8601', '207401', '056201', '004C00', '023B01',
    '012E01', '011D01', '011301'
  ]
};
Map.addLayer(scale_fit, viz, 'LAI Trend');
