var region = ee.Geometry.Polygon([[116.0,39.5],[117.0,39.5],[117.0,40.0],[116.0,40.0]]);

var srtm = ee.Image('USGS/SRTMGL1_003');
var elevation = srtm.select('elevation').clip(region);
var slope = ee.Terrain.slope(elevation).clip(region);

var population_density = ee.ImageCollection('WorldPop/GP/100m/pop')
  .filterDate('2020-01-01','2020-12-31')
  .mean()
  .clip(region);

var sample = slope.addBands(population_density).sample({
  region: region,
  scale: 100,
  numPixels: 5000
});
